import React , { useState, useEffect }from 'react';
import data from "../src/API/data.json"
import JobBoardComponent from "../src/components/JobBoardComponent";

const App = () => {
  const [ jobs, setJobs ] = useState([]);
  const [filters, setFilters] = useState([]);

  useEffect(() => {
    return setJobs(data);
  }, [])

  const filterByTags = ({role, level, tools, languages}) => {
    if(filters.length===0){
      return true;
    }

    const tags = [role, level];

    if(tools) {
      tags.push(...tools);
    }
    if(languages) {
      tags.push(...languages)
    }

    return tags.some(tag => filters.includes(tag))
  }
  const filteredJobs = jobs.filter(filterByTags)

  const handleTagClick = (tag) => {
    if(filters.includes(tag)) return;
    setFilters([...filters, tag]);
  }

  const handleFilterClick = (fil) => {
    setFilters(filters.filter(f => f !== fil));
  }

  return (
    <div className="App">
      <header className="bg-teal-500 mb-12">
        <img src="/images/bg-header-desktop.svg" alt="header"/>
      </header>
        {
          filters.length > 0 && 
          <div className="flex flex-col bg-white shadow-md my-16 mx-10 p-6 rounded">
          filters.map((filter) => <span onClick={() => handleFilterClick(filter)} key={filter} className="text-teal-500 bg-teal-100 font-bold mr-4 mb-4 p-2 rounded sm:mb-0 cursor-pointer">{filter}</span>)
          </div>
        }
      {
        jobs.length === 0 ? (
          <p>Jobs are fetching...</p>
        ) : (
          filteredJobs.map(job => {
            return <JobBoardComponent handleTagClick={handleTagClick} job={job} key={job.id}/>
          })
        )
      }
    </div>
  );
}

export default App;
