import React , { useState, useEffect }from 'react';
import data from "../src/API/data.json"
import JobBoardComponent from "../src/components/JobBoardComponent";

const App = () => {
  const [ jobs, setJobs ] = useState([]);
  const [filters, setFilters] = useState([]);

  useEffect(() => {
    return setJobs(data);
  }, [])

  const filterByTags = ({role, level, tools, languages}) => {
    const tags = [role, level];
    if(filters.length===0){
      return true;
    }

    if(tools) {
      tags.push(...tools);
    }
    if(languages) {
      tags.push(...languages)
    }

    return tags.some(tag => filters.includes(tag))
  }
  const filteredJobs = jobs.filter(filterByTags)

  return (
    <div className="App">
      <header className="bg-teal-500 mb-12">
        <img src="/images/bg-header-desktop.svg"/>
      </header>
      {
        jobs.length === 0 ? (
          <p>Jobs are fetching...</p>
        ) : (
          jobs.map(job => {
            return <JobBoardComponent job={job} key={job.id}/>
          })
        )
      }
    </div>
  );
}

export default App;
