import React from "react";

const JobBoardComponent = ({job}) => {
    const langAndTools = [];

    if(job.tools) {
        langAndTools.push(...job.tools);
    }

    if(job.languages) {
        langAndTools.push(...job.languages);
    }


    return (
        <div className='flex bg-white shadow-md my-4 p-4'>
            <div>
                <img src={job.logo} alt={job.company}/>
            </div>
            <div className="flex flex-col justify-between ml-4">
                <h3 className="font-bold text-teal-500">{job.company}</h3>
                <h2 className="font-bold text-2xl">{job.position}</h2>
                <p className="text-gray-700">
                    {job.postedAt} · {job.contract} · {job.location}
                </p>
            </div>
            <div className="flex items-center ml-auto">
                { langAndTools ? langAndTools.map((langAndTool) => <span className="text-teal-500 bg-teal-100 font-bold m-1 p-2 rounded">{langAndTool}</span>) : '' }
            </div>
        </div>
    )
}

export default JobBoardComponent;
